WebLink.url:
var ids = {!GETRECORDIDS($ObjectType.OpportunityLineItem)}; 

var url = '{!URLFOR($Site.Prefix)}/apex/ia_crm__OpportunityProductsBulkUpdate?recordId={!Opportunity.Id}&recordType={!Opportunity.RecordTypeId}&hasOpportunityLine={!if(Opportunity.HasOpportunityLineItem,'true','false')}';   

var isValid = true;
if (ids.length) { 
if (ids.length <= 50) { 
url += '&oproductIds=' + ids.join(','); 
} else { 
alert('You have selected more than 50 items, please select less items.'); 
isValid=false;
} 
}
if(isValid) {
window.location = url;
}
