CustomField.formula:
IF 
    (Quote.Opportunity.RecordType.DeveloperName = 'Cloud',    
        IF(
            AND (NOT(ISBLANK(Quote.Opportunity.Contract_Start_Date_forward__c)),NOT(ISBLANK(Quote.Opportunity.Contract_End_date_forward__c))),
            TEXT( 
                 (Quote.Opportunity.Contract_End_date_forward__c  - Quote.Opportunity.Contract_Start_Date_forward__c) +                   
                 (Quote.Opportunity.Account.Enhancement_Date__c -  Created_Date2__c)
                ),
            TEXT(
                 Quote.Opportunity.Account.Enhancement_Date__c -  Created_Date2__c
                )
        ),        
        IF(
            AND (NOT(ISBLANK(Quote.Opportunity.Contract_Start_Date_forward__c)),NOT(ISBLANK(Quote.Opportunity.Contract_End_date_forward__c  ))),
            TEXT( 
                 (Quote.Opportunity.Contract_End_date_forward__c  - Quote.Opportunity.Contract_Start_Date_forward__c) +                   
                 (Quote.Opportunity.Parent_Opportunity__r.Account.Enhancement_Date__c - Created_Date2__c)
                ),
            TEXT(
                 Quote.Opportunity.Parent_Opportunity__r.Account.Enhancement_Date__c - Created_Date2__c
                )
        )
    )
